body.script:
$(function () {

  $("#btn-regulations").on('click', function (event) {
    event.preventDefault();
    $("#frm-regulations").submit();
  });

  $("#btn-contact").on('click', function (event) {
    event.preventDefault();
    $("#contact_popup").show();
    $(".overlay2").show();
  });

  $("#close_contact").on("click", function () {
    $("#contact_popup").hide();
    $(".overlay2").hide();
  });

  if( $(window).outerWidth() < 768 ){
    setTimeout(() => {
      console.log('here')
      $('#popup').addClass('popup--visible');
    }, 15000);
  }
});
